import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SeatMap from './SeatMap';
import './App.css';

function App() {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [config, setConfig] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Cargar configuraci√≥n y eventos al iniciar
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);

        // Obtener configuraci√≥n (public key)
        const configResponse = await axios.get('http://localhost:3001/events/config');
        setConfig(configResponse.data);

        // Obtener lista de eventos
        const eventsResponse = await axios.get('http://localhost:3001/events');
        setEvents(eventsResponse.data.events || []);
      } catch (err) {
        setError('Error al cargar datos: ' + err.message);
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const handleEventSelect = (event) => {
    setSelectedEvent(event);
    setSelectedSeats([]);
  };

  const handleSeatSelection = (selectedObjects) => {
    setSelectedSeats(selectedObjects);
    console.log('Asientos seleccionados:', selectedObjects);
  };

  const handleReserveSeats = async () => {
    if (!selectedEvent || selectedSeats.length === 0) return;

    try {
      const seatLabels = selectedSeats.map(seat => seat.label);
      await axios.post(`http://localhost:3001/events/${selectedEvent.key}/reserve`, {
        objects: seatLabels
      });

      const message = config?.demoMode 
        ? `${selectedSeats.length} asiento(s) reservado(s) en MODO DEMO!`
        : `${selectedSeats.length} asiento(s) reservado(s) exitosamente!`;
      alert(message);
      setSelectedSeats([]);
    } catch (err) {
      alert('Error al reservar asientos: ' + err.message);
    }
  };

  const handleBookSeats = async () => {
    if (!selectedEvent || selectedSeats.length === 0) return;

    try {
      const seatLabels = selectedSeats.map(seat => seat.label);
      await axios.post(`http://localhost:3001/events/${selectedEvent.key}/book`, {
        objects: seatLabels
      });

      const message = config?.demoMode 
        ? `${selectedSeats.length} asiento(s) comprado(s) en MODO DEMO!`
        : `${selectedSeats.length} asiento(s) comprado(s) exitosamente!`;
      alert(message);
      setSelectedSeats([]);
    } catch (err) {
      alert('Error al comprar asientos: ' + err.message);
    }
  };

  const createSampleEvent = async () => {
    try {
      const eventData = {
        chartKey: 'sample-chart', // Necesitas tener un chart creado en Seat.io
        eventKey: 'evento-' + Date.now(),
        name: 'Evento de Prueba'
      };

      const response = await axios.post('http://localhost:3001/events', eventData);
      setEvents([...events, response.data]);

      const message = config?.demoMode 
        ? 'Evento demo creado exitosamente!'
        : 'Evento creado exitosamente!';
      alert(message);
    } catch (err) {
      alert('Error al crear evento: ' + err.message);
    }
  };

  if (loading) {
    return (
      <div className="app">
        <div className="loading">
          <h2>Cargando Seat.io POC...</h2>
          <p>Aseg√∫rate de que el backend est√© ejecut√°ndose en el puerto 3001</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <div className="error">
          <h2>‚ùå Error</h2>
          <p>{error}</p>
          <button onClick={() => window.location.reload()}>Reintentar</button>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <header className="app-header">
        <h1>üé´ Seat.io POC</h1>
        <p>Prueba de concepto para reserva de asientos</p>
        {config?.demoMode && (
          <div className="demo-banner">
            üß™ <strong>MODO DEMO</strong> - Usando datos simulados. 
            <br />
            Para usar Seat.io real, configura tus API keys en el backend.
          </div>
        )}
      </header>

      <div className="app-content">
        <div className="sidebar">
          <div className="events-section">
            <h3>üìÖ Eventos Disponibles</h3>
            {events.length === 0 ? (
              <div className="no-events">
                <p>No hay eventos disponibles</p>
                <button onClick={createSampleEvent} className="btn btn-primary">
                  Crear Evento de Prueba
                </button>
              </div>
            ) : (
              <div className="events-list">
                {events.map((event) => (
                  <div
                    key={event.key}
                    className={`event-item ${selectedEvent?.key === event.key ? 'selected' : ''}`}
                    onClick={() => handleEventSelect(event)}
                  >
                    <h4>{event.name || event.key}</h4>
                    <p>Clave: {event.key}</p>
                    {config?.demoMode && (
                      <span className="demo-tag">DEMO</span>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>

          {selectedSeats.length > 0 && (
            <div className="selected-seats">
              <h3>ü™ë Asientos Seleccionados</h3>
              <div className="seats-list">
                {selectedSeats.map((seat, index) => (
                  <span key={index} className="seat-label">
                    {seat.label}
                  </span>
                ))}
              </div>
              <div className="seat-actions">
                <button 
                  onClick={handleReserveSeats} 
                  className="btn btn-warning"
                >
                  üîí Reservar ({selectedSeats.length})
                </button>
                <button 
                  onClick={handleBookSeats} 
                  className="btn btn-success"
                >
                  üí≥ Comprar ({selectedSeats.length})
                </button>
              </div>
            </div>
          )}
        </div>

        <div className="main-content">
          {selectedEvent && config ? (
            <div className="seatmap-container">
              <h2>Mapa de Asientos - {selectedEvent.name || selectedEvent.key}</h2>
              {config.demoMode ? (
                <div className="demo-seatmap">
                  <div className="demo-message">
                    <h3>üé≠ Vista Previa del Mapa de Asientos</h3>
                    <p>En modo demo, el mapa interactivo de Seat.io requiere API keys reales.</p>
                    <p>Configura tus credenciales para ver el mapa real.</p>
                    <div className="demo-actions">
                      <button className="btn btn-primary" onClick={() => setSelectedSeats([
                        {label: 'A-1'}, {label: 'A-2'}, {label: 'A-3'}
                      ])}>
                        ü™ë Simular Selecci√≥n (A-1, A-2, A-3)
                      </button>
                    </div>
                  </div>
                </div>
              ) : (
                <SeatMap
                  publicKey={config.publicKey}
                  eventKey={selectedEvent.key}
                  onSelectionChange={handleSeatSelection}
                />
              )}
            </div>
          ) : (
            <div className="no-event-selected">
              <h2>Selecciona un evento</h2>
              <p>Elige un evento de la lista para ver el mapa de asientos</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
