import React, { useEffect, useRef } from 'react';

const SeatMap = ({ publicKey, eventKey, onSelectionChange }) => {
  const seatMapRef = useRef(null);

  useEffect(() => {
    // Limpiar mapa anterior si existe
    if (seatMapRef.current && typeof seatMapRef.current.destroy === 'function') {
      try {
        seatMapRef.current.destroy();
        seatMapRef.current = null;
      } catch (e) {
        console.log('Mapa ya destruido:', e.message);
      }
    }

    // CDN oficial para la región SA (Sud América) - usando chart.js
    const script = document.createElement('script');
    script.src = 'https://cdn-sa.seatsio.net/chart.js';
    script.async = true;

    script.onload = () => {
      if (window.seatsio) {
        console.log('Seat.io script cargado, creando mapa para evento:', eventKey);
        console.log('Public Key:', publicKey?.substring(0, 8) + '...');

        // Usar SeatingChart según la documentación oficial
        seatMapRef.current = new window.seatsio.SeatingChart({
          divId: 'seatmap',
          workspaceKey: publicKey,
          event: eventKey,
          session: 'continue',
          priceFormatter: price => price ? `$${price}` : 'Gratis',
          onObjectSelected: (object) => {
            console.log('Asiento seleccionado:', object);
            if (onSelectionChange && seatMapRef.current) {
              const selectedObjects = seatMapRef.current.selectedObjects || [];
              onSelectionChange(selectedObjects);
            }
          },
          onObjectDeselected: (object) => {
            console.log('Asiento deseleccionado:', object);
            if (onSelectionChange && seatMapRef.current) {
              const selectedObjects = seatMapRef.current.selectedObjects || [];
              onSelectionChange(selectedObjects);
            }
          },
          onObjectClicked: (object) => {
            console.log('Click en objeto:', object);
          },
          onRenderStarted: () => {
            console.log('Renderizado iniciado');
          },
          onRenderFinished: () => {
            console.log('Renderizado terminado');
          },
          onChartRendered: (chart) => {
            console.log('Chart renderizado:', chart);
          },
          showZoomOutButtonOnMobile: true,
          showFullScreenButton: true,
          loading: 'Cargando mapa de asientos...',
          language: 'es'
        }).render();
      } else {
        console.error('window.seatsio no está disponible');
      }
    };

    script.onerror = (error) => {
      console.error('Error al cargar el script de Seat.io:', error);
    };

    document.head.appendChild(script);

    // Cleanup
    return () => {
      if (seatMapRef.current && typeof seatMapRef.current.destroy === 'function') {
        try {
          seatMapRef.current.destroy();
        } catch (e) {
          console.log('Error al destruir el mapa:', e.message);
        }
        seatMapRef.current = null;
      }
      // Remover script si aún existe
      const existingScript = document.querySelector('script[src="https://cdn-sa.seatsio.net/chart.js"]');
      if (existingScript && existingScript.parentNode) {
        existingScript.parentNode.removeChild(existingScript);
      }
    };
  }, [publicKey, eventKey, onSelectionChange]);

  return (
    <div className="seatmap-wrapper">
      <div 
        id="seatmap" 
        style={{ 
          width: '100%', 
          height: '600px',
          border: '1px solid #ddd',
          borderRadius: '8px',
          overflow: 'hidden',
          backgroundColor: '#f8f9fa'
        }} 
      />
      <div className="seatmap-legend">
        <div className="legend-item">
          <span className="legend-color available"></span>
          <span>Disponible</span>
        </div>
        <div className="legend-item">
          <span className="legend-color selected"></span>
          <span>Seleccionado</span>
        </div>
        <div className="legend-item">
          <span className="legend-color unavailable"></span>
          <span>No disponible</span>
        </div>
      </div>
    </div>
  );
};

export default SeatMap;
