import React, { useEffect, useRef } from 'react';

const SeatMap = ({ publicKey, eventKey, onSelectionChange }) => {
  const seatMapRef = useRef(null);

  useEffect(() => {
    // Limpiar mapa anterior si existe
    if (seatMapRef.current) {
      seatMapRef.current.destroy();
    }

    const script = document.createElement('script');
    script.src = 'https://cdn.seats.io/js/renderer.js';
    script.async = true;

    script.onload = () => {
      if (window.seatsio) {
        seatMapRef.current = new window.seatsio.SeatMap({
          divId: 'seatmap',
          publicKey: publicKey,
          event: eventKey,
          priceFormatter: price => price ? `$${price}` : 'Gratis',
          multiSelectEnabled: true,
          onObjectSelected: (object) => {
            console.log('Asiento seleccionado:', object);
            if (onSelectionChange) {
              // Obtener todos los objetos seleccionados
              const selectedObjects = seatMapRef.current.getSelectedObjects();
              onSelectionChange(selectedObjects);
            }
          },
          onObjectDeselected: (object) => {
            console.log('Asiento deseleccionado:', object);
            if (onSelectionChange) {
              // Obtener todos los objetos seleccionados
              const selectedObjects = seatMapRef.current.getSelectedObjects();
              onSelectionChange(selectedObjects);
            }
          },
          onObjectClicked: (object) => {
            console.log('Click en objeto:', object);
          },
          colors: {
            colorScheme: 'light'
          },
          showZoomOutButtonOnMobile: true,
          showFullScreenButton: true,
          loading: 'Cargando mapa de asientos...',
          availableCategories: [],
          unavailableCategories: []
        }).render();
      }
    };

    script.onerror = () => {
      console.error('Error al cargar el script de Seat.io');
    };

    document.body.appendChild(script);

    // Cleanup
    return () => {
      if (seatMapRef.current) {
        seatMapRef.current.destroy();
      }
      // Remover script si aún existe
      const existingScript = document.querySelector('script[src="https://cdn.seats.io/js/renderer.js"]');
      if (existingScript) {
        document.body.removeChild(existingScript);
      }
    };
  }, [publicKey, eventKey, onSelectionChange]);

  return (
    <div className="seatmap-wrapper">
      <div 
        id="seatmap" 
        style={{ 
          width: '100%', 
          height: '600px',
          border: '1px solid #ddd',
          borderRadius: '8px',
          overflow: 'hidden'
        }} 
      />
      <div className="seatmap-legend">
        <div className="legend-item">
          <span className="legend-color available"></span>
          <span>Disponible</span>
        </div>
        <div className="legend-item">
          <span className="legend-color selected"></span>
          <span>Seleccionado</span>
        </div>
        <div className="legend-item">
          <span className="legend-color unavailable"></span>
          <span>No disponible</span>
        </div>
      </div>
    </div>
  );
};

export default SeatMap;
